import React, { useState } from 'react';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import FileUpload from './components/FileUpload';
import FileList from './components/FileList';
import PDFViewer from './components/PDFViewer';
import Chat from './components/Chat';

const API_URL = process.env.REACT_APP_API_URL || 'http://localhost:8000';
const WS_URL = process.env.REACT_APP_WS_URL || 'ws://localhost:8000/ws';

const App: React.FC = () => {
  const [selectedFile, setSelectedFile] = useState<string>('');
  const [showChat, setShowChat] = useState<boolean>(false);

  const handleFileUpload = (fileName: string) => {
    setSelectedFile(fileName);
    setShowChat(true);
    toast.success(`文件 ${fileName} 上传成功!`);
  };

  const handleFileSelect = (fileName: string) => {
    setSelectedFile(fileName);
    setShowChat(true);
  };

  const handleError = (error: string) => {
    toast.error(error);
  };

  return (
    <div className="flex h-screen bg-gray-100">
      <div className="w-1/4 p-4 border-r border-gray-300">
        <h1 className="text-2xl font-bold mb-4">Chat with PDF</h1>
        <FileUpload onFileUploaded={handleFileUpload} onError={handleError} apiUrl={API_URL} />
        <FileList onFileSelect={handleFileSelect} onError={handleError} apiUrl={API_URL} />
      </div>
      <div className="w-3/4 flex">
        {showChat ? (
          <>
            <div className="w-1/2 p-4 border-r border-gray-300">
              <PDFViewer fileName={selectedFile} onError={handleError} apiUrl={API_URL} />
            </div>
            <div className="w-1/2 p-4">
              <Chat selectedFile={selectedFile} onError={handleError} wsUrl={WS_URL} />
            </div>
          </>
        ) : (
          <div className="w-full flex items-center justify-center">
            <p className="text-xl text-gray-500">请上传或选择一个PDF文件开始聊天</p>
          </div>
        )}
      </div>
      <ToastContainer position="top-right" autoClose={5000} />
    </div>
  );
};

export default App;
