import React, { useCallback, useState } from 'react';
import { useDropzone } from 'react-dropzone';
import axios from 'axios';

interface FileUploadProps {
  onFileUploaded: (fileName: string) => void;
}

const FileUpload: React.FC<FileUploadProps> = ({ onFileUploaded }) => {
  const [uploading, setUploading] = useState<boolean>(false);

  const onDrop = useCallback(async (acceptedFiles: File[]) => {
    const file = acceptedFiles[0];
    if (file) {
      setUploading(true);
      const formData = new FormData();
      formData.append('file', file);
      try {
        const response = await axios.post('http://localhost:8000/upload-pdf', formData, {
          headers: { 'Content-Type': 'multipart/form-data' },
        });
        onFileUploaded(response.data.file_name);
      } catch (error) {
        console.error('Error uploading file:', error);
      } finally {
        setUploading(false);
      }
    }
  }, [onFileUploaded]);

  const { getRootProps, getInputProps, isDragActive } = useDropzone({
    onDrop,
    accept: 'application/pdf',
    multiple: false
  });

  return (
    <div {...getRootProps()} className="border-2 border-dashed border-gray-300 rounded-lg p-8 text-center cursor-pointer hover:border-blue-500 transition duration-300">
      <input {...getInputProps()} />
      {uploading ? (
        <p className="text-blue-600">上传中...</p>
      ) : isDragActive ? (
        <p className="text-blue-600">将文件拖放到此处...</p>
      ) : (
        <>
          <p className="text-gray-600 mb-2">拖放 PDF 文件到此处，或点击选择文件</p>
          <button className="bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600 transition duration-300">
            选择 PDF 文件
          </button>
        </>
      )}
    </div>
  );
};

export default FileUpload;
