import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import FileUpload from '../components/FileUpload';

const UploadPage: React.FC = () => {
  const [uploadStatus, setUploadStatus] = useState<string>('');
  const navigate = useNavigate();

  const handleFileUploaded = (fileName: string) => {
    setUploadStatus(`文件 ${fileName} 上传成功!`);
    setTimeout(() => {
      navigate(`/chat/${fileName}`);
    }, 2000);
  };

  const handleChatClick = () => {
    navigate('/chat');
  };

  return (
    <div className="flex flex-col items-center justify-center min-h-screen bg-gray-100">
      <div className="w-full max-w-md">
        <h1 className="text-4xl font-bold mb-8 text-center text-blue-600">Chat with PDF</h1>
        <div className="bg-white p-8 rounded-lg shadow-md">
          <FileUpload onFileUploaded={handleFileUploaded} />
          {uploadStatus && (
            <p className="mt-4 text-green-600 text-center">{uploadStatus}</p>
          )}
        </div>
        <button
          onClick={handleChatClick}
          className="mt-6 w-full bg-blue-600 text-white px-4 py-2 rounded-lg hover:bg-blue-700 transition duration-300"
        >
          Chat with PDF
        </button>
      </div>
    </div>
  );
};

export default UploadPage;
