import React, { useState, useEffect, useRef, useCallback } from 'react';

interface Message {
  role: 'user' | 'assistant';
  content: string;
}

interface ChatProps {
  selectedFile: string;
  onError: (error: string) => void;
  wsUrl: string;
}

const Chat: React.FC<ChatProps> = ({ selectedFile, onError, wsUrl }) => {
  const [messages, setMessages] = useState<Message[]>([]);
  const [input, setInput] = useState('');
  const [connectionStatus, setConnectionStatus] = useState<'连接中' | '已连接' | '已断开'>('连接中');
  const [isReconnecting, setIsReconnecting] = useState(false);
  const ws = useRef<WebSocket | null>(null);
  const chatContainerRef = useRef<HTMLDivElement>(null);
  const reconnectTimeoutRef = useRef<NodeJS.Timeout | null>(null);
  const reconnectAttempts = useRef(0);
  const maxReconnectAttempts = 5;
  const reconnectInterval = 3000;

  const connectWebSocket = useCallback(() => {
    if (ws.current?.readyState === WebSocket.OPEN) {
      return;
    }

    setConnectionStatus('连接中');
    ws.current = new WebSocket(wsUrl);

    ws.current.onopen = () => {
      setConnectionStatus('已连接');
      reconnectAttempts.current = 0;
      setIsReconnecting(false);
    };

    ws.current.onclose = (event) => {
      setConnectionStatus('已断开');
      if (!event.wasClean) {
        handleReconnect();
      }
    };

    ws.current.onerror = (error) => {
      console.error('WebSocket错误:', error);
      setConnectionStatus('已断开');
      onError('WebSocket连接错误');
      handleReconnect();
    };

    ws.current.onmessage = (event) => {
      try {
        const data = JSON.parse(event.data);
        if (data.error) {
          onError(data.error);
        } else {
          setMessages(prev => [...prev, { role: 'assistant', content: data.response }]);
        }
      } catch (error) {
        console.error('解析消息时出错:', error);
        onError('解析服务器响应时出错');
      }
    };
  }, [onError]);

  const handleReconnect = useCallback(() => {
    if (reconnectAttempts.current < maxReconnectAttempts) {
      setIsReconnecting(true);
      if (reconnectTimeoutRef.current) {
        clearTimeout(reconnectTimeoutRef.current);
      }
      reconnectTimeoutRef.current = setTimeout(() => {
        reconnectAttempts.current += 1;
        connectWebSocket();
      }, reconnectInterval);
    } else {
      setIsReconnecting(false);
      setConnectionStatus('已断开');
      onError('无法重新连接到服务器');
    }
  }, [connectWebSocket, onError]);

  useEffect(() => {
    connectWebSocket();
    return () => {
      if (ws.current) {
        ws.current.close();
      }
      if (reconnectTimeoutRef.current) {
        clearTimeout(reconnectTimeoutRef.current);
      }
    };
  }, [selectedFile, connectWebSocket]);

  useEffect(() => {
    if (chatContainerRef.current) {
      chatContainerRef.current.scrollTop = chatContainerRef.current.scrollHeight;
    }
  }, [messages]);

  const sendMessage = useCallback(() => {
    if (input.trim() && connectionStatus === '已连接') {
      const newMessage: Message = { role: 'user', content: input };
      setMessages(prev => [...prev, newMessage]);
      ws.current?.send(JSON.stringify({ query: input, file_name: selectedFile, chat_history: messages }));
      setInput('');
    } else if (connectionStatus !== '已连接') {
      onError('WebSocket未连接,无法发送消息');
    }
  }, [input, connectionStatus, selectedFile, messages, onError]);

  return (
    <div className="flex flex-col h-full">
      <div className="flex-grow overflow-auto mb-4 p-4 bg-white rounded-lg shadow" ref={chatContainerRef}>
        {messages.map((message, index) => (
          <div key={index} className={`mb-4 ${message.role === 'user' ? 'text-right' : 'text-left'}`}>
            <span className={`inline-block p-2 rounded-lg ${message.role === 'user' ? 'bg-blue-500 text-white' : 'bg-gray-200 text-black'}`}>
              {message.content}
            </span>
          </div>
        ))}
      </div>
      <div className="flex flex-col">
        <div className="flex mb-2">
          <input
            type="text"
            value={input}
            onChange={(e) => setInput(e.target.value)}
            onKeyPress={(e) => e.key === 'Enter' && sendMessage()}
            className="flex-grow border border-gray-300 rounded-l-lg p-2 focus:outline-none focus:ring-2 focus:ring-blue-500"
            placeholder="输入您的问题..."
            disabled={connectionStatus !== '已连接'}
          />
          <button 
            onClick={sendMessage}
            className={`px-4 py-2 rounded-r-lg transition duration-300 ${
              connectionStatus === '已连接' 
                ? 'bg-blue-500 text-white hover:bg-blue-600' 
                : 'bg-gray-300 text-gray-500 cursor-not-allowed'
            }`}
            disabled={connectionStatus !== '已连接'}
          >
            发送
          </button>
        </div>
        <div className={`text-sm ${
          connectionStatus === '已连接' ? 'text-green-500' : 
          connectionStatus === '连接中' ? 'text-yellow-500' : 'text-red-500'
        }`}>
          {connectionStatus === '已连接' ? '已连接' : 
           connectionStatus === '连接中' ? '正在连接...' : 
           isReconnecting ? `连接断开,正在尝试重新连接(尝试 ${reconnectAttempts.current}/${maxReconnectAttempts})...` :
           '连接断开,请刷新页面重试'}
        </div>
      </div>
    </div>
  );
};

export default Chat;
